insert into book (name, author, description, publisher, isbn, year)
values ('Гаррі Поттер і філософський камінь', 'Джоан Ролінґ', 'Це перша книга про Гаррі Поттера. Відірвавшись від комп''ютерів і телеекранів, сотні мільйонів дітей та дорослих у всьому світі ЗНОВУ ЧИТАЮТЬ! Такого успіху не знала жодна художня книга. Казка про хлопчика-чарівника Гаррі Поттера зачарувала всю планету...', 'А-БА-БА-ГА-ЛА-МА-ГА', '978-966-7047-39-9', 2002),
       ('Гаррі Поттер і таємна кімната', 'Джоан Ролінґ', 'Це друга книга про пригоди Гаррі Поттера. Він знову вступає у відчайдушну сутичку зі злом. Проте цього разу ворог такий сильний, що надії на перемогу майже немає...', 'А-БА-БА-ГА-ЛА-МА-ГА', '978-966-7047-34-4', 2002),
       ('Гаррі Поттер і смертельні реліквії', 'Джоан Ролінґ',
        'Гаррі звалив на свої плечі таємниче, небезпечне і майже нездійсненне завдання: знайти і знищити решту Волдемортових горокраксів. Ще ніколи Гаррі не почувався таким самотнім, а його майбутнє ще ніколи не здавалося йому таким похмурим... У цій заключній і чи не найяскравішій зі всієї «поттеріани» книзі Дж. К. Ролінґ дає довгоочікувані відповіді на багато запитань. Її захоплююча манера оповіді з численними несподіваними сюжетними поворотами дарує неймовірну насолоду мільйонам читачів — тим, які трималися з Гаррі аж до самого кінця. ',
        'А-БА-БА-ГА-ЛА-МА-ГА', '978-966-7047-70-2', 2007),
       ('Гаррі Поттер і прокляте дитя', 'Джоан Ролінґ',
        'Бути Гаррі Поттером нелегко, а надто тепер, коли він — працівник Міністерства магії, трудоголік, одружений батько трьох школяриків. Доки Гаррі Поттер бореться зі своїм мину­­лим, яке ніяк не хоче відпускати, його молодшому синові Албусові доводиться миритися з тягарем свого знаменитого роду, — спадком, якого він не обирав. У той час, як минуле зловісно змішалося з майбутнім, батько і син пізнають сумну істину: інколи темрява виходить звідти, де її ніхто не чекав.', 'А-БА-БА-ГА-ЛА-МА-ГА',
        '978-617-585-112-8', 2016),
       ('Гаррі Поттер і Орден Фенікса', 'Джоан Ролінґ', 'ніхто й не сподівався, що п’ятий рік навчання у Ґогвортсі буде для Гаррі та його друзів легкою прогулянкою. Проте запровадження в школі посади Верховного інквізитора ще більше все ускладнило... ', 'А-БА-БА-ГА-ЛА-МА-ГА', '978-966-7047-42-9', 2003),
       ('Гаррі Поттер і напівкровний Принц', 'Джоан Ролінґ', 'Це шоста книга про Гаррі Поттера. Як швидко плине час! Із кожною новою книжкою про Гаррі Поттера сотні мільйонів читачів у світі дорослішають разом з головним героєм. Наш Гаррі, здається, закохався... А тим часом темні сили на чолі з лордом Волдемортом отримують могутню підмогу... ', 'А-БА-БА-ГА-ЛА-МА-ГА', '978-966-7047-29-0', 2005),
       ('Гаррі Поттер і келих вогню', 'Джоан Ролінґ', 'Попереду у Гаррі Поттера четвертий рік навчання у Ґогвортській школі чарів та чаклунства, на яку несподівано насувається дивовижна подія — Тричаклунський турнір. І на жаль, не лише він...', 'А-БА-БА-ГА-ЛА-МА-ГА', '978-966-7047-40-5', 2003),
       ('Гаррі Поттер і в’язень Азкабану', 'Джоан Ролінґ', 'Пригоди Гаррі Поттера тривають. Разом з Роном і Герміоною, своїми найкращими друзями, він уже третій рік навчається у Ґогвортській школі чарівників. На Гаррі насувається страшенна небезпека; усі про це знають, але ніхто не в змозі допомогти...', 'А-БА-БА-ГА-ЛА-МА-ГА', '978-966-7047-36-8', 2002),
       ('Де немає Бога', 'Макс Кідрук', 'Люди — соціальні істоти. Існування суспільства неможливе без співпраці, розуміння та дотримання справедливості. Та чи існує це все поза соціумом, коли людина опиняється на межі життя та смерті?
Доведена до відчаю українка з великою сумою грошей, яка ігнорує дзвінки від чоловіка. Росіянин-пілот, який намагається приборкати аерофобію після загибелі коханки в катастрофі рейсу MH17. Найвідоміший політик Баварії на пікові кар’єри, який ненавидить свою роботу. Гравець американської Національної футбольної ліги, життя якого розвалилося після одного невдалого розіграшу. Таємний папський кардинал, який прямує до країни, де офіційно не існує Католицької церкви... Незнайомці з різними долями. Єдине спільне для них — рейс 341, і він веде до найбільшого випробування у їхньому житті.',
        'Клуб Сімейного Дозвілля', '978-617-12-4950-9', 2018),
       ('Доки світло не згасне назавжди', 'Макс Кідрук', 'Ніякі жертви не варті спроб виправити минуле, адже безжальний світ завжди знайде спосіб примусити за них розплачуватися.
Сестри Рута й Індія, попри незвичні імена, проживають цілком буденні життя: Індія навчається на лікаря, Рута закінчує одинадцятий клас. Усе раптово змінюється, коли через нещасний випадок гине наречений Індії. Раніше близькі сестри віддаляються, звинувачуючи одна одну в тому, що сталося. І саме тоді, коли, здавалося б, із конфлікту вже не виплутатися, Рута виявляє, що здатна впливати на реальність у снах. Невдовзі в її сновидіннях з’являються химерні тіні, і що ближче вони підступають, то дужче мерхне світло довкола дівчини. За нестримним бажанням повернути колишнє життя Рута не помічає, як кожен наступний сон стає дедалі темнішим.',
        'Клуб Сімейного Дозвілля', '978-617-12-6116-7', 2019),
       ('Не озирайся і мовчи', 'Макс Кідрук', 'Уявіть, що на Землі існує місце, яке ніби застигло в часі. Місце, здатне сховати будь-кого, хто прагне втекти від реальності. Уявіть, що для того, аби туди потрапити, достатньо не озиратися й мовчати. Є лише одна проблема: в такому місці часом з’являються речі, страшніші за те, від чого ховаєшся.
Життя сім’ї Грозанів раптово змінюється. Вони переїжджають до нового будинку, і чотирнадцятирічний Марк змушений піти до нової школи. Хлопець начитаний, розумний, але дуже сором’язливий — ідеальний об’єкт для цькування. Щодень більше замикаючись в собі, він мріє про світ, де немає знущань і безпричинної жорстокості. Одного дня Марк знайомиться із Сонею, дівчиною з паралельного класу, яка розповідає йому про існування такого світу і про можливість туди потрапити… просто скориставшись ліфтом у їхньому будинку. Хлопець не розуміє, навіщо Соня вигадує нісенітниці, які легко спростувати, але зрештою вирушає за нею. Він не підозрює, що звичайна мандрівка на десятий поверх загрожує чимось набагато гіршим за руйнування уявлень про реальний світ. ',
        'Клуб Сімейного Дозвілля', '978-617-12-3865-7', 2017),
       ('Антикрихкість. Про (не)вразливе у реальному житті', 'Насім Ніколас Талеб ',
        'Наше життя неможливе без хаосу, мінливості та стресу, які постійно тиснуть на нас. В умовах невизначеності ми прагнемо сталості, однак саме вона стає перешкодою на шляху до розвитку. Дивовижно, та завдяки невизначеності і впливу змін люди, речі та цілі системи можуть еволюціонувати й ставати кращими. Цю рису Насім Талеб називає антикрихкістю й окреслює прості правила, які допоможуть обернути непередбачуваність і невизначеність на джерело самовдосконалення. Ба більше, ця властивість дає користь як людям, так і компаніям і навіть країнам, а коло питань, які допомагає з’ясувати книжка, надзвичайно широке — від методу помилок, оцінювання ризику до політики, освіти й медицини',
        'Наш Формат', '978-617-7973-00-2 ', 2021),
       ('Простими словами. Як розібратися у своїх емоціях', 'Марк Лівін , Ілля Полудьонний ', 'Розум: та все добре! У тебе є робота, дах над головою, їжа і друзі.
Емоції: щось так тоскно.
Якщо ви переживали схоже й не могли втямити, у чому річ — ця книжка для вас. У ній журналіст Марк Лівін і психотерапевт Ілля Полудьонний простими словами розповідають про емоції та почуття. Вони вчать розшифровувати повідомлення, що приходять у чат із самим собою: «Ти вже кілька днів сумуєш. Зверни на це увагу». Або: «Здається, ти заздриш другові, який знайшов нову роботу. Може, тобі теж час щось змінити?». Структурно книжка складається з 20 розділів. Кожен присвячується окремому почуттю чи емоції: злості, заздрості, образі, сорому, самотності тощо. ',
        'Наш Формат', '978-617-7866-60-1 ', 2020),
       ('12 правил життя. Як перемогти хаос', 'Джордан Пітерсон ', '
У сучасному світі толерантність часто підміняє мораль, спільноти — індивідуальність, а повага до думки інших — готовність захищати власну позицію. Принаймні, так вважає автор книги «12 правил життя» Джордан Пітерсон. Скандально відомий своїми поглядами, автор впевнений: без чітких норм моралі людство втрачає своє призначення та напрямок розвитку.
У своїй книзі Джордан Пітерсон не лише критикує людей за відсутність чітких моральних установок, а і закликає згадати про індивідуальну відповідальність. Для цього він розробив дванадцять правил, що вкажуть правильний шлях та допоможуть перемогти хаос повсякдення. ', 'Наш Формат', '978-617-768-232-4', 2019),
       ('Політики не брешуть. 10 законів взаємодії політиків і виборців', 'Вадим Денисенко , Юрій Вишневський', 'Чому в липні 1945-го, через два місяці після перемоги над Німеччиною, Черчилль зазнав поразки на виборах? Чому в Польщі так довго триває епоха Дуди, а в Угорщині — Орбана? Чому навесні 2019-го Порошенко зазнав поразки, а Зеленський опинився в кріслі президента?
Таких питань безліч, і відповіді на них можна шукати в безлічі випадковостей. Проте розгадка всіх цих перемог і поразок — у політологічних законах, які діють у політиці з такою самою невідворотною силою, що й фізичні закони в природі', 'Наш Формат', '978-617-7863-41-9', 2020),
       ('Навчитися вчитися. Як запустити свій мозок на повну', 'Барбара Оклі',
        'Ви математик чи гуманітарій? Професорка Барбара Оклі переконує, що таких понять не існує. Кожен із нас має внутрішній потенціал, щоб засвоїти будь-які знання. Головне — навчитися вчитися й запустити свій мозок на повну. Навіть більше, одноманітний і цілеспрямований рух до розв’язання проблеми — не завжди найкращий шлях. Інколи варто зробити крок назад, щоб поглянути на завдання з іншого боку, запустити свою креативність, навіть якщо ви працюєте над розв’язанням математичної задачі', 'Наш Формат',
        '978-617-7552-87-0', 2018),
       ('Точка неповернення', ' 	Дмитро Вербич', 'Ця історія про сучасних українських лицарів з різним способом життя: вегетаріанця, який торгував соляркою в Нігерії, тренера з фрі-файту, мандрівника й багатьох інших. Проте в одну мить для них настала точка неповернення, після якої життя вже не може бути таким, як до того. Почалася війна', 'Наш Формат', '978-617-7863-09-9', 2020),
       ('Усе замахало. Але надія є', 'Марк Менсон',
        'Ми живемо в парадоксі прогресу: що кращими стають речі навколо нас, то тривожнішими ми є. І хоча світ успішно бореться з голодом, расизмом, сексизмом та іншими викликами, люди й далі почуваються нещасними. То що ж відбувається? На думку Марка Менсона, людству потрібен не порядок, добробут чи нові моделі електрокарів. Йому не вистачає чогось значно ефемернішого — надії. Надії, яка ґрунтується на відчутті, що ми контролюємо ситуацію, діємо за власними цінностями й належимо до спільноти', 'Наш Формат',
        '978-617-7730-91-9', 2019),
       ('Я змішаю твою кров із вугіллям', 'Олександр Михед',
        'Наприкінці 2016 року Олександр Михед вирушив у подорож шістьма містами Донецької та Луганської областей, досліджуючи тамтешнє життя, а також історії, заховані в архівах. Проте найважливіше для автора — почути голоси місцевих жителів. Розкопуючи пласти історії та нашарування міфів, письменник розповідає дивовижну історію Сходу, приборкати який намагалися британські й бельгійські інвестори, імперські та радянські правителі. Як одна з найстаріших черепах у світі пов’язана із заснуванням Донецька? Якою ціною дається кожна тонна видобутого вугілля? І які історії заховано у вільних степах Дикого поля?',
        'Наш Формат', '978-617-7866-33-5 ', 2020),
       ('Підкори свій Еверест. Як поразку перетворити на перемогу', 'Тетяна Яловчак', 'На що ви готові, щоб досягнути своєї мети? Тетяна Яловчак продала квартиру, щоб стати першою українкою, яка піднялася на Еверест. Та за крок до перемоги вона не стала влаштовувати перегони з конкуренткою, бо гора і так забрала багато життів. Утім Тетяна не здалася і таки дійшла до вершини. А через рік стала першою українкою-рекордсменкою, яка підкорила найвищі точки всіх континентів', 'Наш Формат', '978-617-7866-39-7', 2020),
       ('Мислення швидке й повільне', 'Деніел Канеман',
        'Зазвичай ми вважаємо, що діємо раціонально. Але чи справді це так? На основі досліджень із когнітивної та соціальної психології Деніел Канеман розкриває таємниці роботи людського мозку, описує неусвідомлені упередження, властивості інтуїтивного та усвідомленого мислення, роль емоцій в оцінних судженнях. Без цих знань ви ризикуєте зробити неправильний вибір у магазині, інвестувати в неуспішні проекти й загалом приймати неправильні рішення. «Мислення швидке й повільне» навчить вас ефективно користуватися обмеженим ресурсом уваги, ухвалювати рішення в умовах невизначеності й удосконалювати власні судження. Утім книжка не є сухою інструкцією з експлуатації мозку. Це психологічна драма з двома дійовими особами у вашій голові — інтуїтивним та усвідомленим мисленням',
        'Наш Формат', '978-617-7279-18-0', 2017);